function adjustContentMargin(){const content=document.documentElement,footerHeight=document.getElementById("footerBorder").offsetHeight,headerHeight=document.getElementById("header").offsetHeight,contentHeight=content.offsetHeight,viewportHeight=window.innerHeight,remainingSpace=viewportHeight-contentHeight-footerHeight;document.getElementById("footerBorder").style.marginTop=remainingSpace>0?`${remainingSpace}px`:"0"}window.addEventListener("load",()=>{window.addEventListener("resize",adjustContentMargin),window.addEventListener("load",adjustContentMargin),adjustContentMargin()});