function updateSlide(thumb){document.getElementById("thumb-highlighted").removeAttribute("id"),thumb.parentElement.className.includes("glide__slide")?thumb.parentElement.setAttribute("id","thumb-highlighted"):thumb.setAttribute("id","thumb-highlighted");for(let x=0;x<solutionPanels.length;x++){const panel=solutionPanels[x];thumb.getAttribute("data-id")==panel.getAttribute("data-id")&&(document.getElementById("active").removeAttribute("id"),panel.setAttribute("id","active"))}}window.addEventListener("load",()=>{thumbs=document.querySelectorAll(".selector-thumb"),solutionPanels=document.querySelectorAll(".solution-panel");for(let i=0;i<thumbs.length;i++)thumbs[i].addEventListener("click",()=>{updateSlide(thumbs[i])}),console.log(thumbs[i]);var glide=new Glide(".glide",{type:"carousel",perView:4,breakpoints:{800:{perView:3},500:{perView:2,focusAt:0},350:{perView:1}}});glide.mount(),glide.on("run.after",(function(){active=document.querySelector(".glide__slide--active"),updateSlide(active.querySelector(".selector-thumb"))}))});